import React from 'react';
import { Line } from 'react-chartjs-2';
import { Box, Paper, Typography, useTheme } from '@mui/material';

const CagrChart = ({ incomeStatements }) => {
  const theme = useTheme();

  const yearlyData = incomeStatements
    .filter(item => item.type === 'yearly')
    .sort((a, b) => new Date(a.date) - new Date(b.date));

  const years = yearlyData.map(financial => new Date(financial.date).getFullYear());

  const data = {
    labels: years, // Use the 'years' array as labels
    datasets: [
      {
        label: 'Total Revenue',
        data: yearlyData.map(financial => parseFloat(financial.total_revenue)),
        borderColor: theme.palette.primary.main,
        backgroundColor: theme.palette.primary.light,
        fill: false,
      },
      {
        label: 'Gross Profit',
        data: yearlyData.map(financial => parseFloat(financial.gross_profit)),
        borderColor: theme.palette.secondary.main,
        backgroundColor: theme.palette.secondary.light,
        fill: false,
      },
      {
        label: 'Net Income',
        data: yearlyData.map(financial => parseFloat(financial.net_income)),
        borderColor: theme.palette.success.main,
        backgroundColor: theme.palette.success.light,
        fill: false,
      },
    ],
  };

  const options = {
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Yearly Financials Over 5 Years',
      },
    },
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      y: {
        beginAtZero: false,
        ticks: {
          callback: function (value, index, ticks) {
            return value / 1e9 + 'B';
          },
        },
      },
      x: {
        type: 'time', // Use 'time' scale type
        time: {
          unit: 'year', // Display labels by year
          displayFormats: {
            year: 'YYYY', // Format for displaying years
          },
          tooltipFormat: 'YYYY', // Format for tooltip
        },
        title: {
          display: true,
          text: 'Year',
        },
      },
    },
  };

  return (
    <Paper elevation={3}>
      <Box sx={{ padding: theme.spacing(3) }}>
        <Typography variant="h6" gutterBottom>
          5 Year Financials
        </Typography>
        <Box sx={{ height: '300px', mt: theme.spacing(3) }}>
          <Line data={data} options={options} />
        </Box>
      </Box>
    </Paper>
  );
};

export default CagrChart;
