import React from 'react';
import { Typography, Table, TableBody, TableCell, TableContainer, TableRow, Paper, Box } from '@mui/material';

const StockHighlights = ({ highlights }) => {
  // Extract currency symbol from the highlights data
  const currencySymbol = highlights.general.currency_symbol;

  // Create an array of custom highlights with key, label, and transform function
  const customHighlights = [
    {
      key: "market_capitalization",
      label: "Market Cap",
      transform: (value) =>
        `${currencySymbol}${Number(value).toLocaleString()}`, // Add currency symbol and formatting
    },
    {
      key: "pe_ratio",
      label: "P/E Ratio",
      transform: (value) =>
        `${currencySymbol}${Number(value).toFixed(2)}`, // Add currency symbol and format as a fixed number with 2 decimal places
    },
    // Add more custom entries as needed
  ];

  return (
    <Box>
      <Box sx={{ bgcolor: 'primary.main', p: 1 }}>
        <Typography variant="subtitle1" sx={{ color: 'common.white', textAlign: 'center' }}>Highlights</Typography>
      </Box>
      <TableContainer component={Paper}>
        <Table aria-label="simple table">
          <TableBody>
            {customHighlights.map(({ key, label, transform }) => (
              <TableRow key={key}>
                <TableCell component="th" scope="row">{label}</TableCell>
                <TableCell align="right">
                  {transform ? transform(highlights[key]) : highlights[key]}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default StockHighlights;
