import React, { useState, useEffect } from 'react';
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from '@mui/material';

const FinancialsTable = ({ data }) => {
  const [selectedFinancial, setSelectedFinancial] = useState('Income Statement');
  const [selectedFrequency, setSelectedFrequency] = useState('Yearly');
  const [financialData, setFinancialData] = useState([]);

  useEffect(() => {
    // Logic to fetch financial data based on selectedFinancial and selectedFrequency
    fetchData(selectedFinancial, selectedFrequency);
  }, [selectedFinancial, selectedFrequency]);

  const fetchData = (financialType, frequency) => {
    // Here, you should write logic to fetch data from the appropriate table based on the selectedFinancial and selectedFrequency.
    // You can make API requests or use data provided via props, depending on your setup.

    // Example:
    if (financialType === 'Income Statement') {
      // Fetch data from the income statement table
      const incomeStatementData = data.income_statements.filter(item => item.type === frequency);
      setFinancialData(incomeStatementData);
    } else if (financialType === 'Balance Sheet') {
      // Fetch data from the balance sheet table
      const balanceSheetData = data.balance_sheets.filter(item => item.type === frequency);
      setFinancialData(balanceSheetData);
    } else if (financialType === 'Cash Flow Statement') {
      // Fetch data from the cash flow statement table
      const cashFlowData = data.cash_flows.filter(item => item.type === frequency);
      setFinancialData(cashFlowData);
    }
  };

  const handleFinancialChange = (financial) => {
    setSelectedFinancial(financial);
  };

  const handleFrequencyChange = (frequency) => {
    setSelectedFrequency(frequency);
  };

  return (
    <div>
      <div>
        {/* Buttons to select financial type */}
        <Button
          variant={selectedFinancial === 'Income Statement' ? 'contained' : 'outlined'}
          onClick={() => handleFinancialChange('Income Statement')}
        >
          Income Statement
        </Button>
        <Button
          variant={selectedFinancial === 'Balance Sheet' ? 'contained' : 'outlined'}
          onClick={() => handleFinancialChange('Balance Sheet')}
        >
          Balance Sheet
        </Button>
        <Button
          variant={selectedFinancial === 'Cash Flow Statement' ? 'contained' : 'outlined'}
          onClick={() => handleFinancialChange('Cash Flow Statement')}
        >
          Cash Flow Statement
        </Button>
      </div>

      <div>
        {/* Buttons to select frequency */}
        <Button
          variant={selectedFrequency === 'Yearly' ? 'contained' : 'outlined'}
          onClick={() => handleFrequencyChange('Yearly')}
        >
          Yearly
        </Button>
        <Button
          variant={selectedFrequency === 'Quarterly' ? 'contained' : 'outlined'}
          onClick={() => handleFrequencyChange('Quarterly')}
        >
          Quarterly
        </Button>
      </div>

      {/* Table to display financial data */}
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              {/* Add headers based on your financial data */}
              <TableCell>Date</TableCell>
              <TableCell>Value 1</TableCell>
              <TableCell>Value 2</TableCell>
              {/* Add more headers based on your financial data */}
            </TableRow>
          </TableHead>
          <TableBody>
            {financialData.map((item, index) => (
              <TableRow key={index}>
                {/* Add columns based on your financial data */}
                <TableCell>{item.date}</TableCell>
                <TableCell>{item.value1}</TableCell>
                <TableCell>{item.value2}</TableCell>
                {/* Add more columns based on your financial data */}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default FinancialsTable;
