import React, { useState, useEffect } from 'react';
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from '@mui/material';
import { useTheme } from '@mui/material';

const FinancialsTable = ({ incomeStatements, balanceSheets, cashFlows }) => {
  const theme = useTheme();
  const [selectedFinancial, setSelectedFinancial] = useState('Income Statement');
  const [selectedFrequency, setSelectedFrequency] = useState('Yearly');
  const [financialData, setFinancialData] = useState([]);

  useEffect(() => {
    const fetchData = () => {
      let dataToDisplay = [];

      if (selectedFinancial === 'Income Statement') {
        dataToDisplay = incomeStatements.filter(item => item.type === selectedFrequency);
      } else if (selectedFinancial === 'Balance Sheet') {
        dataToDisplay = balanceSheets.filter(item => item.type === selectedFrequency);
      } else if (selectedFinancial === 'Cash Flow Statement') {
        dataToDisplay = cashFlows.filter(item => item.type === selectedFrequency);
      }

      setFinancialData(dataToDisplay);
    };

    fetchData(); // Call the fetchData function when selectedFinancial or selectedFrequency changes
  }, [selectedFinancial, selectedFrequency, incomeStatements, balanceSheets, cashFlows]);

  const handleFinancialChange = (financial) => {
    setSelectedFinancial(financial);
  };

  const handleFrequencyChange = (frequency) => {
    setSelectedFrequency(frequency);
  };

  return (
    <div>
      <div>
        {/* Buttons to select financial type */}
        <Button
          variant={selectedFinancial === 'Income Statement' ? 'contained' : 'outlined'}
          onClick={() => handleFinancialChange('Income Statement')}
        >
          Income Statement
        </Button>
        <Button
          variant={selectedFinancial === 'Balance Sheet' ? 'contained' : 'outlined'}
          onClick={() => handleFinancialChange('Balance Sheet')}
        >
          Balance Sheet
        </Button>
        <Button
          variant={selectedFinancial === 'Cash Flow Statement' ? 'contained' : 'outlined'}
          onClick={() => handleFinancialChange('Cash Flow Statement')}
        >
          Cash Flow Statement
        </Button>
      </div>

      <div>
        {/* Buttons to select frequency */}
        <Button
          variant={selectedFrequency === 'Yearly' ? 'contained' : 'outlined'}
          onClick={() => handleFrequencyChange('Yearly')}
        >
          Yearly
        </Button>
        <Button
          variant={selectedFrequency === 'Quarterly' ? 'contained' : 'outlined'}
          onClick={() => handleFrequencyChange('Quarterly')}
        >
          Quarterly
        </Button>
      </div>

      {/* Table to display financial data */}
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              {/* Add headers based on your financial data */}
              <TableCell>Date</TableCell>
              <TableCell>Value 1</TableCell>
              <TableCell>Value 2</TableCell>
              {/* Add more headers based on your financial data */}
            </TableRow>
          </TableHead>
          <TableBody>
            {financialData.map((item, index) => (
              <TableRow key={index}>
                {/* Add columns based on your financial data */}
                <TableCell>{item.date}</TableCell>
                <TableCell>{item.value1}</TableCell>
                <TableCell>{item.value2}</TableCell>
                {/* Add more columns based on your financial data */}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default FinancialsTable;
