import React from 'react';
import { Typography, Table, TableBody, TableCell, TableContainer, TableRow, Paper, Box } from '@mui/material';

const customHighlights = [
  { key: "wall_street_target_price", label: "Target Price" },
  { key: "dividend_yield", label: "Dividend" },
  { key: "market_capitalization", label: "Market Cap" },
  { key: "pe_ratio", label: "P/E Ratio" },
  { key: "peg_ratio", label: "P/E Growth Ratio" },
  // Add more custom entries as needed
];

const StockHighlights = ({ highlights }) => {
  // Function to format the keys that have numeric values
  const formatValue = (value) => {
    if (!isNaN(value)) {
      return parseFloat(value).toLocaleString();
    }
    return value;
  };

  return (
    <Box>
      <Box sx={{ bgcolor: 'primary.main', p: 1 }}>
        <Typography variant="subtitle1" sx={{ color: 'common.white', textAlign: 'center' }}>Highlights</Typography>
      </Box>
      <TableContainer component={Paper}>
        <Table aria-label="simple table">
          <TableBody>
            {customHighlights.map(({ key, label }, index) => (
              <TableRow key={key}>
                {index % 2 === 0 && (
                  <TableCell component="th" scope="row">{label}</TableCell>
                )}
                <TableCell align="right" sx={{ fontWeight: 'bold' }}>
                  {formatValue(highlights[key])}
                </TableCell>
                {index % 2 !== 0 && (
                  <TableCell component="th" scope="row">{customHighlights[index + 1]?.label}</TableCell>
                )}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default StockHighlights;
