import React, { useState, useEffect } from 'react';
import { Drawer, Button, List, ListItem, ListItemText, TextField, IconButton, Typography, ListItemSecondaryAction, ListItemIcon } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

const MyNotesDrawer = ({ open, onClose, stockId }) => {
  const [notes, setNotes] = useState([]);
  const [noteText, setNoteText] = useState('');

  useEffect(() => {
    // Fetch notes from the backend when the drawer opens
    if (open) {
      fetch(`http://localhost:8000/notes/?stock=${stockId}`, {
        method: 'GET',
        headers: {
          'Authorization': `Token <your_token_here>`, // Replace with your token
        },
      })
        .then(response => response.json())
        .then(data => setNotes(data))
        .catch(error => console.error('Error fetching notes:', error));
    }
  }, [open, stockId]);

  const addNote = () => {
    if (noteText.trim() !== '') {
      const newNote = {
        stock: stockId,
        content: noteText,
      };

      fetch('https://django-stocks-ecbc6bc5e208.herokuapp.com/notes/', {
        method: 'POST',
        headers: {
          'Authorization': `Token <your_token_here>`, // Replace with your token
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newNote),
      })
        .then(response => response.json())
        .then(data => {
          setNotes([...notes, data]);
          setNoteText('');
        })
        .catch(error => console.error('Error adding note:', error));
    }
  };

  const deleteNote = (noteId) => {
    fetch(`https://django-stocks-ecbc6bc5e208.herokuapp.com/notes/${noteId}/`, {
      method: 'DELETE',
      headers: {
        'Authorization': `Token <your_token_here>`, // Replace with your token
      },
    })
      .then(() => {
        // Remove the note from the notes array
        setNotes(notes.filter(note => note.id !== noteId));
      })
      .catch(error => console.error('Error deleting note:', error));
  };

  return (
    <Drawer anchor="right" open={open} onClose={onClose}>
      <div style={{ width: 350, padding: 16 }}>
        <IconButton onClick={onClose} style={{ position: 'absolute', right: 8, top: 8 }}>
          <CloseIcon />
        </IconButton>
        <Typography variant="h6" style={{ marginBottom: 16 }}>My Notes</Typography>
        <TextField
          label="Add a note"
          fullWidth
          value={noteText}
          onChange={(e) => setNoteText(e.target.value)}
          variant="outlined"
          style={{ marginBottom: 16 }}
        />
        <Button
          variant="contained"
          color="primary"
          startIcon={<AddIcon />}
          onClick={addNote}
          fullWidth
        >
          Add Note
        </Button>
        <List>
          {notes.map((note) => (
            <ListItem key={note.id}>
              <ListItemText
                primary={note.content}
                secondary={new Date(note.created_at).toLocaleDateString()}
              />
              <ListItemIcon>
                <IconButton edge="end" onClick={() => deleteNote(note.id)}>
                  <DeleteIcon />
                </IconButton>
              </ListItemIcon>
            </ListItem>
          ))}
        </List>
      </div>
    </Drawer>
  );
};

export default MyNotesDrawer;
