import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import { Box, Paper, Typography, useTheme } from '@mui/material';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const CagrChart = ({ financials }) => {
  const theme = useTheme();

  const data = {
    labels: financials.map(financial => financial.date),
    datasets: [
      {
        label: 'Total Revenue',
        data: financials.map(financial => financial.total_revenue),
        backgroundColor: theme.palette.primary.main,
      },
      {
        label: 'Gross Profit',
        data: financials.map(financial => financial.gross_profit),
        backgroundColor: theme.palette.secondary.main,
      },
      {
        label: 'Net Income',
        data: financials.map(financial => financial.net_income),
        backgroundColor: theme.palette.success.main,
      }
    ],
  };

  const options = {
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: '5 Year CAGR',
      },
    },
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Amount'
        }
      }
    }
  };

  return (
    <Paper elevation={3}>
      <Box sx={{ padding: theme.spacing(3) }}>
        <Typography variant="h6" gutterBottom>
          Revenue, GP, and Net Income over 5 Years
        </Typography>
        <Box sx={{ height: '400px', mt: theme.spacing(3) }}>
          <Bar data={data} options={options} />
        </Box>
      </Box>
    </Paper>
  );
};

export default CagrChart;
