import React, { useState } from 'react';
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper
} from '@mui/material';

const FinancialsTable = ({ incomeStatements = [], balanceSheets = [], cashFlows = [] }) => {
  const [selectedFinancial, setSelectedFinancial] = useState('Income Statement');
  const [selectedFrequency, setSelectedFrequency] = useState('Yearly');

  // Determine which data to use based on selectedFinancial and selectedFrequency
  const financialData = (() => {
    switch (selectedFinancial) {
      case 'Income Statement':
        return incomeStatements.filter(item => item.type === selectedFrequency);
      case 'Balance Sheet':
        return balanceSheets.filter(item => item.type === selectedFrequency);
      case 'Cash Flow':
        return cashFlows.filter(item => item.type === selectedFrequency);
      default:
        return [];
    }
  })();

  return (
    <>
      {/* Buttons to select financial type */}
      <Button
        variant={selectedFinancial === 'Income Statement' ? 'contained' : 'outlined'}
        onClick={() => setSelectedFinancial('Income Statement')}
      >
        Income Statement
      </Button>
      <Button
        variant={selectedFinancial === 'Balance Sheet' ? 'contained' : 'outlined'}
        onClick={() => setSelectedFinancial('Balance Sheet')}
      >
        Balance Sheet
      </Button>
      <Button
        variant={selectedFinancial === 'Cash Flow' ? 'contained' : 'outlined'}
        onClick={() => setSelectedFinancial('Cash Flow')}
      >
        Cash Flow
      </Button>

      {/* Buttons to select frequency */}
      <Button
        variant={selectedFrequency === 'Yearly' ? 'contained' : 'outlined'}
        onClick={() => setSelectedFrequency('Yearly')}
      >
        Yearly
      </Button>
      <Button
        variant={selectedFrequency === 'Quarterly' ? 'contained' : 'outlined'}
        onClick={() => setSelectedFrequency('Quarterly')}
      >
        Quarterly
      </Button>

      {/* Table to display financial data */}
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              {/* Dynamically create table header based on the keys of the financial data */}
              {financialData.length > 0 && Object.keys(financialData[0]).map((key, index) => (
                <TableCell key={index}>{key.replace(/_/g, ' ').toUpperCase()}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {financialData.map((item, index) => (
              <TableRow key={index}>
                {/* Dynamically create table cells based on the values of the financial data */}
                {Object.values(item).map((value, cellIndex) => (
                  <TableCell key={cellIndex}>
                    {typeof value === 'number' ? value.toLocaleString() : value}
                  </TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
};

export default FinancialsTable;
