import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import BaseLayout from './layouts/BaseLayout';
import SignIn from './pages/Login';
import SignUp from './pages/SignUp';
import StockDetail from './pages/StockDetailPage';
import HomePage from './pages/HomePage';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Helmet } from 'react-helmet';
import './assets/styles/custom.css';
import { AuthProvider } from './context/AuthContext';
import Follow from './pages/Following';
import DividendScreener from './pages/DividendScreener';
import ContactForm from './pages/ContactUs';
import ForgotPassword from './pages/ForgotPassword';  
import ResetPassword from './pages/ResetPassword';
import ProtectedRoute from './components/common/ProtectedRoute';
import RestrictedRoute from './components/common/RestrictedRoute';


function App() {
  return (
    <>
    <AuthProvider>
      <Helmet>
        <link href="https://fonts.googleapis.com/css2?family=Roboto:wght@300;400;500;600;700&display=swap" rel="stylesheet" />
      </Helmet>
      <Router>
        <BaseLayout>
<Routes>
              {/* Accessible to all */}
              <Route path="/" element={<HomePage />} />
              <Route path="/stocks/:primary_ticker" element={<StockDetail />} />
              <Route path="/dividend-screener" element={<DividendScreener />} />
              <Route path="/contact" element={<ContactForm />} />

              {/* Accessible only when not logged in */}
              <Route path="/login" element={
                !isAuthenticated ? <SignIn /> : <Navigate to="/" />
              } />
              <Route path="/signup" element={
                !isAuthenticated ? <SignUp /> : <Navigate to="/" />
              } />
              <Route path="/forgot-password" element={
                !isAuthenticated ? <ForgotPassword /> : <Navigate to="/" />
              } />
              <Route path="/reset-password/:uidb64/:token" element={
                !isAuthenticated ? <ResetPassword /> : <Navigate to="/" />
              } />

              {/* Accessible only when logged in */}
              <Route path="/following" element={
                isAuthenticated ? <Follow /> : <Navigate to="/login" />
              } />
              {/* ... other routes ... */}
            </Routes>
        </BaseLayout>
      </Router>
      </AuthProvider>
    </>
  );
}

export default App;
