import React, { useEffect, useState } from 'react';
import './Ticker.css'; // You can style it according to your design

const Ticker = () => {
  const [stocks, setStocks] = useState([]);
  const [colors, setColors] = useState({}); // Store color (red or green) for each stock

  useEffect(() => {
    // Fetch stock data from your API
    fetch('https://script.googleusercontent.com/macros/echo?user_content_key=c8tWHwXIx4vz_zdJewMMVNZzcj-52oWRSjf42V0rQwnxCSE5S7SO4HyeWAn9Jl_lssgsdwW3o8MTnhWLGQwtQRAh0uNuO2Sgm5_BxDlH2jW0nuo2oDemN9CCS2h10ox_1xSncGQajx_ryfhECjZEnPeTRfTtTl31E7n6HlPkwXGCVvT2mUROOOaCQFgyCI-jNC3U55BpMAcbf-OKdJkmAjb8iiC6b9mkVfxHOPGXTDlwvughaiZuIQ&lib=MRK9JCV_hCBBxn1ilxmzDhCwsu2EIHoHF')
      .then((response) => response.json())
      .then((data) => {
        // Process the data and set it in the state
        const processedData = data.map((stock) => {
          // Determine the color based on percent change
          const color = stock.Percent < 0 ? 'red' : 'green';
          return {
            ...stock,
            color,
          };
        });
        setStocks(processedData);
      })
      .catch((error) => console.error('Error fetching stock data:', error));
  }, []);

  return (
    <div className="ticker-tape">
      {stocks.map((stock, index) => (
        <div
          key={index}
          className={`stock-card ${stock.color}`}
        >
          <div className="stock-name">{stock.Name}</div>
          <div className="stock-price">{stock.Price}</div>
          <div className="stock-percent">
            {stock.Percent}% <span className="arrow">â–²</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Ticker;
