import React from 'react';
import { Typography, Table, TableBody, TableCell, TableContainer, TableRow, Paper, Box, Grid } from '@mui/material';

const StockHighlights = ({ highlights }) => {
  // Extract currency symbol from the highlights data
  const currencySymbol = highlights.currency_symbol;

  // Create an array of custom highlights with key, label, and transform function
  const customHighlights = [
    {
      key: "market_capitalization",
      label: "Market Cap",
      transform: (value) => `${currencySymbol}${value}`, // No formatting here
    },
    {
      key: "pe_ratio",
      label: "P/E Ratio",
      transform: (value) => `${currencySymbol}${value}`,
    },
    // Add more custom entries as needed
  ];

  // Create two arrays, each containing key-value pairs
  const firstColumn = customHighlights.slice(0, customHighlights.length / 2);
  const secondColumn = customHighlights.slice(customHighlights.length / 2);

  return (
    <Box>
      <Box sx={{ bgcolor: 'primary.main', p: 1 }}>
        <Typography variant="subtitle1" sx={{ color: 'common.white', textAlign: 'center' }}>Highlights</Typography>
      </Box>
      <TableContainer component={Paper}>
        <Table aria-label="simple table">
          <TableBody>
            <TableRow>
              <TableCell colSpan={2} align="center"></TableCell>
              <TableCell colSpan={2} align="center"></TableCell>
            </TableRow>
            {firstColumn.map(({ key, label, transform }) => (
              <TableRow key={key}>
                <TableCell component="th" scope="row">{label}</TableCell>
                <TableCell align="right"><b>{transform ? transform(highlights[key]) : highlights[key]}</b></TableCell>
                {/* Empty cells for the second column */}
                <TableCell />
                <TableCell />
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default StockHighlights;
