import React, { useState, useEffect } from 'react';
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper
} from '@mui/material';

const FinancialsTable = ({ incomeStatements, balanceSheets, cashFlows }) => {
  const [selectedType, setSelectedType] = useState('income_statements');
  const [selectedFrequency, setSelectedFrequency] = useState('yearly');
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    let data = [];
    if (selectedType === 'income_statements') {
      data = incomeStatements;
    } else if (selectedType === 'balance_sheets') {
      data = balanceSheets;
    } else if (selectedType === 'cash_flows') {
      data = cashFlows;
    }
    const filtered = data?.filter(item => item.type.toLowerCase() === selectedFrequency);
    setFilteredData(filtered);
  }, [incomeStatements, balanceSheets, cashFlows, selectedType, selectedFrequency]);

  if (!filteredData || filteredData.length === 0) {
    return <div>No data available for the selected type and frequency.</div>;
  }

  return (
    <>
      {/* Buttons to select financial type */}
      <Button variant={selectedType === 'income_statements' ? 'contained' : 'outlined'} onClick={() => setSelectedType('income_statements')}>
        Income Statement
      </Button>
      <Button variant={selectedType === 'balance_sheets' ? 'contained' : 'outlined'} onClick={() => setSelectedType('balance_sheets')}>
        Balance Sheet
      </Button>
      <Button variant={selectedType === 'cash_flows' ? 'contained' : 'outlined'} onClick={() => setSelectedType('cash_flows')}>
        Cash Flow
      </Button>

      {/* Buttons to select frequency */}
      <Button variant={selectedFrequency === 'yearly' ? 'contained' : 'outlined'} onClick={() => setSelectedFrequency('yearly')}>
        Yearly
      </Button>
      <Button variant={selectedFrequency === 'quarterly' ? 'contained' : 'outlined'} onClick={() => setSelectedFrequency('quarterly')}>
        Quarterly
      </Button>

      {/* Table to display filtered financial data */}
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              {/* Dynamically create table header based on the keys of the financial data */}
              {Object.keys(filteredData[0]).map((key, index) => (
                <TableCell key={index}>{key.replace(/_/g, ' ').toUpperCase()}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {filteredData.map((item, index) => (
              <TableRow key={index}>
                {/* Dynamically create table cells based on the values of the financial data */}
                {Object.entries(item).map(([key, value], cellIndex) => (
                  <TableCell key={cellIndex}>
                    {typeof value === 'number' ? value.toLocaleString() : value}
                  </TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
};

export default FinancialsTable;
