import React, { useState, useEffect } from 'react';
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper
} from '@mui/material';

const FinancialsTable = ({ financialData }) => {
  const [selectedType, setSelectedType] = useState('income_statements');
  const [selectedFrequency, setSelectedFrequency] = useState('yearly');
  const [displayData, setDisplayData] = useState([]);

  useEffect(() => {
    if (financialData) {
      // Filter the selected type and frequency
      const filteredData = financialData[selectedType].filter(item => item.type === selectedFrequency);
      setDisplayData(filteredData);
    }
  }, [financialData, selectedType, selectedFrequency]);

  if (!financialData) {
    return <div>Loading financial data...</div>;
  }

  if (displayData.length === 0) {
    return <div>No financial data available for the selected type and frequency.</div>;
  }

  // Define the function to change financial type
  const changeFinancialType = (type) => {
    setSelectedType(type);
  };

  return (
    <>
      {/* Buttons to select financial type */}
      <Button variant={selectedType === 'income_statements' ? 'contained' : 'outlined'} onClick={() => changeFinancialType('income_statements')}>
        Income Statement
      </Button>
      <Button variant={selectedType === 'balance_sheets' ? 'contained' : 'outlined'} onClick={() => changeFinancialType('balance_sheets')}>
        Balance Sheet
      </Button>
      <Button variant={selectedType === 'cash_flows' ? 'contained' : 'outlined'} onClick={() => changeFinancialType('cash_flows')}>
        Cash Flow
      </Button>

      {/* Buttons to select frequency */}
      <Button variant={selectedFrequency === 'yearly' ? 'contained' : 'outlined'} onClick={() => setSelectedFrequency('yearly')}>
        Yearly
      </Button>
      <Button variant={selectedFrequency === 'quarterly' ? 'contained' : 'outlined'} onClick={() => setSelectedFrequency('quarterly')}>
        Quarterly
      </Button>

      {/* Table to display financial data */}
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              {/* Dynamically create table header based on the keys of the financial data */}
              {Object.keys(displayData[0]).map((key, index) => (
                <TableCell key={index}>{key.replace(/_/g, ' ').toUpperCase()}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {displayData.map((item, index) => (
              <TableRow key={index}>
                {/* Dynamically create table cells based on the values of the financial data */}
                {Object.entries(item).map(([key, value], cellIndex) => (
                  <TableCell key={cellIndex}>
                    {typeof value === 'number' ? value.toLocaleString() : value}
                  </TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
};

export default FinancialsTable;
