import React from 'react';
import {
  Typography,
  Paper,
  Box,
  Skeleton
} from '@mui/material';

const Description = ({ text }) => {
  return (
    <Paper elevation={3} sx={{ margin: 'auto', overflow: 'hidden' }}>
      <Box p={2}>
        {text ? (
          <Typography>{text}</Typography>
        ) : (
          <Skeleton variant="text" width={100} />
        )}
      </Box>
    </Paper>
  );
};

export default Description;
