import React from 'react';
import Typography from '@mui/material/Typography';
import Slider from '@mui/material/Slider';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import { useTheme } from '@mui/material/styles';

const AnalystOverallRating = ({ ratings }) => {
  const theme = useTheme(); // This hook allows you to use the current theme
    const calculateOverallRating = (ratings) => {
    // Define the weight for each rating
    const weights = {
        strong_buy: 1,
        buy: 2,
        hold: 3,
        sell: 4,
        strong_sell: 5
    };

    // Calculate the weighted sum of ratings
    const weightedSum = Object.keys(ratings).reduce((sum, key) => {
        return sum + (weights[key] * ratings[key]);
    }, 0);

    // Calculate the total number of analysts
    const numberOfAnalysts = Object.values(ratings).reduce((sum, num) => sum + num, 0);

    // Return the weighted average rounded to one decimal place
    return (weightedSum / numberOfAnalysts).toFixed(1);
    };

  const overallRating = calculateOverallRating(ratings);

  // Create marks for the Slider
  const marks = [
    { value: 1, label: 'Strong Buy' },
    { value: 2, label: 'Buy' },
    { value: 3, label: 'Hold' },
    { value: 4, label: 'Underperform' },
    { value: 5, label: 'Sell' },
  ];

  return (
    <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
      <Typography id="analyst-rating-slider" gutterBottom>
        Recommendation Rating
      </Typography>
      <Box sx={{ m: 3 }}>
        <Slider
          value={parseFloat(overallRating)}
          aria-labelledby="analyst-rating-slider"
          valueLabelDisplay="auto"
          step={0.1}
          marks={marks}
          min={1}
          max={5}
          disabled
          sx={{
            color: theme.palette.primary.main, // Use the primary color from the theme
            '& .MuiSlider-thumb': {
              backgroundColor: theme.palette.common.white, // Thumb color
            },
            '& .MuiSlider-markLabel': {
              color: theme.palette.text.primary, // Marks labels color
            },
            '& .MuiSlider-valueLabel': {
              backgroundColor: 'transparent',
              color: theme.palette.primary.main,
              '& *': {
                background: 'transparent',
                color: theme.palette.primary.main,
              },
            },
          }}
        />
      </Box>
      <Typography variant="h5" component="h4" align="center" color="primary">
        {`Overall Rating: ${overallRating}`}
      </Typography>
    </Paper>
  );
};

export default AnalystOverallRating;
