import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Slider, Typography, Table, TableBody, TableCell, TableHead, TableRow, Paper, CircularProgress } from '@mui/material';

function DividendScreener() {
    const [stocks, setStocks] = useState([]);
    const [dividendThreshold, setDividendThreshold] = useState(0);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchStockData = async () => {
            try {
                const response = await fetch(`http://127.0.0.1:8000/api/stocks/`, {
                headers: {
                    'Authorization': `Token 13502af70a55d1fcddf7c094e4418c65904ef6eb`,
                },
                })
                
                if (response.data) {
                    setStocks(response.data);
                } else {
                    console.error('Error fetching stock data:', response.status);
                }
                setLoading(false);
            } catch (error) {
                console.error('Error fetching stock data:', error);
                setLoading(false);
            }
        };

        fetchStockData();
    }, []);

    const handleSliderChange = (event, newValue) => {
        setDividendThreshold(newValue);
    };

    return (
        <div>
            <Typography id="dividend-slider" gutterBottom>
                Dividend Yield Threshold: {dividendThreshold}%
            </Typography>
            <Slider
                value={dividendThreshold}
                onChange={handleSliderChange}
                aria-labelledby="dividend-slider"
                valueLabelDisplay="auto"
                step={0.1}
                marks
                min={0}
                max={20} 
            />
            <Paper>
                {loading ? (
                    <CircularProgress />
                ) : stocks.length > 0 ? (
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Name</TableCell>
                                <TableCell align="right">Dividend Yield (%)</TableCell>
                                <TableCell align="right">Market Cap</TableCell>
                                <TableCell align="right">P/E Ratio</TableCell>
                                {/* Add more columns as needed */}
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {stocks.map((stock) => (
                                <TableRow key={stock.id}>
                                    <TableCell component="th" scope="row">
                                        {stock.dividend_data.name}
                                    </TableCell>
                                    <TableCell align="right">{stock.dividend_data.dividend_yield}</TableCell>
                                    <TableCell align="right">{stock.dividend_data.market_capitalization}</TableCell>
                                    <TableCell align="right">{stock.dividend_data.pe_ratio}</TableCell>
                                    {/* Add more cells as needed */}
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                ) : (
                    <Typography>No data available</Typography>
                )}
            </Paper>
        </div>
    );
}

export default DividendScreener;
