import React from 'react';
import Typography from '@mui/material/Typography';
import Slider from '@mui/material/Slider';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import { useTheme } from '@mui/material/styles';

const AnalystOverallRating = ({ ratings }) => {
  const theme = useTheme(); // This hook allows you to use the current theme
    const calculateOverallRating = (ratings) => {
    // Define the weight for each rating
    const weights = {
        strong_buy: 1,
        buy: 2,
        hold: 3,
        sell: 4,
        strong_sell: 5
    };

    // Calculate the weighted sum of ratings
    const weightedSum = Object.keys(ratings).reduce((sum, key) => {
        return sum + (weights[key] * ratings[key]);
    }, 0);

    // Calculate the total number of analysts
    const numberOfAnalysts = Object.values(ratings).reduce((sum, num) => sum + num, 0);

    // Return the weighted average rounded to one decimal place
    return (weightedSum / numberOfAnalysts).toFixed(1);
    };

  const overallRating = calculateOverallRating(ratings);

  // Create marks for the Slider
  const marks = [
    { value: 1, label: 'Strong Buy' },
    { value: 2, label: 'Buy' },
    { value: 3, label: 'Hold' },
    { value: 4, label: 'Underperform' },
    { value: 5, label: 'Sell' },
  ];

  return (
    <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
      <Typography id="analyst-rating-slider" gutterBottom>
        Recommendation Rating
      </Typography>
<Box sx={{ m: 3 }}>
  <Slider
    // ... other Slider props
    valueLabelFormat={(value) => {
      return `Average Rating: ${value} based on ${numberOfAnalysts} opinions`;
    }}
  />
</Box>
<Typography variant="h5" component="h2" align="center" color="primary">
  {`Overall Rating: ${overallRating} based on ${numberOfAnalysts} opinions`}
</Typography>

    </Paper>
  );
};

export default AnalystOverallRating;
