import React from 'react';
import { Typography, Table, TableBody, TableCell, TableContainer, TableRow, Paper, Box } from '@mui/material';

const StockHighlights = ({ highlights, currencySymbol }) => {
  // Define your custom highlights array
  const customHighlights = [
    {
      key: "market_capitalization",
      label: "Market Cap",
      transform: (value) => `${currencySymbol}${Number(value).toLocaleString()}`,
    },
    {
      key: "pe_ratio",
      label: "P/E Ratio",
      transform: (value) => value.toFixed(2), // Assuming P/E ratio doesn't need a currency symbol
    },
    // ...other highlights
  ];

  return (
    <Box>
      <Box sx={{ bgcolor: 'primary.main', p: 1 }}>
        <Typography variant="subtitle1" sx={{ color: 'common.white', textAlign: 'center' }}>Highlights</Typography>
      </Box>
      <TableContainer component={Paper}>
        <Table aria-label="simple table">
          <TableBody>
            {customHighlights.map(({ key, label, transform }) => (
              <TableRow key={key}>
                <TableCell component="th" scope="row">{label}</TableCell>
                <TableCell align="right">
                  {transform(highlights[key])}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default StockHighlights;
