import React from 'react';
import {
  Typography,
  Paper,
  Box,
  Skeleton
} from '@mui/material';

const Description = ({ description }) => {
  const descriptionText = description ? description.text : null;

  return (
    <Paper elevation={3} sx={{ margin: 'auto', overflow: 'hidden' }}>
      <Box sx={{ bgcolor: 'primary.main', p: 1 }}>
        <Typography variant="subtitle1" sx={{ color: 'common.white', textAlign: 'center' }}>Description</Typography>
      </Box>
      <Box sx={{ p: 2 }}>
        {descriptionText ? (
          <Typography variant="body1">
            {descriptionText}
          </Typography>
        ) : (
          <Skeleton variant="text" width={300} animation="wave" />
        )}
      </Box>
    </Paper>
  );
};

export default Description;
