import React from 'react';
import Typography from '@mui/material/Typography';
import Slider from '@mui/material/Slider';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';

const AnalystOverallRating = ({ ratings }) => {
  const calculateOverallRating = (ratings) => {
    const totalRatings = ratings.strong_buy * 1 + ratings.buy * 2 + ratings.hold * 3 + ratings.sell * 4 + ratings.strong_sell * 5;
    const numberOfAnalysts = ratings.strong_buy + ratings.buy + ratings.hold + ratings.sell + ratings.strong_sell;
    return (totalRatings / numberOfAnalysts).toFixed(1);
  };

  const overallRating = calculateOverallRating(ratings);

  return (
    <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
      <Typography id="analyst-rating-slider" gutterBottom>
        Recommendation Rating
      </Typography>
      <Box sx={{ m: 3 }}>
        <Slider
          value={parseFloat(overallRating)}
          aria-labelledby="analyst-rating-slider"
          valueLabelDisplay="auto"
          step={0.1}
          marks
          min={1}
          max={5}
          disabled
        />
      </Box>
      <Typography variant="h5" component="h2" align="center">
        {`Overall Rating: ${overallRating}`}
      </Typography>
    </Paper>
  );
};

export default AnalystOverallRating;
