import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { Slider, Typography, Table, TableBody, TableCell, TableHead, TableRow, Paper, Box, Skeleton, Grid, IconButton, TableContainer } from '@mui/material';
import ReactCountryFlag from "react-country-flag";
import UnfollowIcon from '@mui/icons-material/RemoveCircleOutline';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import { AuthContext } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

function DividendScreener() {
  const [stocks, setStocks] = useState([]);
  const [dividendThreshold, setDividendThreshold] = useState([0, 20]);
  const [payoutRatio, setPayoutRatio] = useState([0, 100]);
  const [peRatio, setPeRatio] = useState([0, 100]);
  const [loading, setLoading] = useState(true);
  const { isAuthenticated } = useContext(AuthContext);
  const navigate = useNavigate();
  const [followedStocks, setFollowedStocks] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const response = await axios.get('https://django-stocks-ecbc6bc5e208.herokuapp.com/api/dividend_data');
        setStocks(response.data.results);
      } catch (error) {
        console.error('Error fetching stocks:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (isAuthenticated) {
      fetch('https://django-stocks-ecbc6bc5e208.herokuapp.com/api/followed_stocks/', {
        headers: {
          'Authorization': `Token 13502af70a55d1fcddf7c094e4418c65904ef6eb`, // Replace with the actual user's authentication token
        },
      })
      .then(response => response.json())
      .then(data => {
        setFollowedStocks(data);
      })
      .catch(error => console.error('Error fetching followed stocks:', error));
    }
  }, [isAuthenticated]);

  const getPayoutRatioStyle = (ratio) => {
    if (ratio > 100) {
      return { color: 'red' }; // Red for high risk
    } else if (ratio >= 60) {
      return { color: 'orange' }; // Amber for moderate risk
    }
    return { color: 'green' }; // Green for low risk
  };

  const getDividendGrowthStyle = (growth) => {
    if (growth < 0) {
      return { color: 'red' }; // Red for negative growth
    } else if (growth === 0) {
      return { color: 'orange' }; // Amber for no growth
    }
    return { color: 'green' }; // Green for positive growth
  };

  const handleChangeDividend = (event, newValue) => {
    setDividendThreshold(newValue);
  };

  const handleChangePayoutRatio = (event, newValue) => {
    setPayoutRatio(newValue);
  };

  const handleChangePERatio = (event, newValue) => {
    setPeRatio(newValue);
  };  

  const formatMarketCap = (value) => {
    return `${(value / 1e9).toFixed(2)}b`;
  };

  const formatPercentage = (value) => {
    return `${(value * 100).toFixed(2)}%`;
  };

  const handleRowClick = (primaryTicker) => {
    navigate(`/stocks/${primaryTicker}`);
  };

  const handleFollowClick = (event, primaryTicker) => {
    event.stopPropagation();
    fetch(`https://django-stocks-ecbc6bc5e208.herokuapp.com/api/stocks/${primaryTicker}/toggle_follow/`, {
      method: 'POST',
      headers: {
        'Authorization': `Token 13502af70a55d1fcddf7c094e4418c65904ef6eb`, // Replace with the actual user's authentication token
      },
    })
    .then(response => response.json())
    .then(data => {
      if (data.status === "ok" && data.action === "followed") {
        setFollowedStocks([...followedStocks, primaryTicker]); // Add the followed stock to the list
      } else if (data.status === "ok" && data.action === "unfollowed") {
        setFollowedStocks(followedStocks.filter(stock => stock !== primaryTicker)); // Remove the unfollowed stock from the list
      } else {
        console.error('Error:', data);
      }
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  const filteredStocks = stocks.filter(stock => {
    const dividendYield = parseFloat(stock.dividend_yield) * 100;
    const payoutRatioValue = parseFloat(stock.payout_ratio) * 100;
    const peRatioValue = parseFloat(stock.pe_ratio);

    const dividendPass = dividendYield >= dividendThreshold[0] && dividendYield <= dividendThreshold[1];
    const payoutRatioPass = payoutRatioValue >= payoutRatio[0] && payoutRatioValue <= payoutRatio[1];
    const peRatioPass = peRatioValue >= peRatio[0] && peRatioValue <= peRatio[1];

    return dividendPass && payoutRatioPass && peRatioPass;
  });

  return (
    <div>
      <Grid container spacing={3} mt={4}>
        <Grid item md={2}>
          <Typography id="dividend-slider" variant="body2" gutterBottom>
            Dividend Yield:<br></br> {dividendThreshold[0]}% - {dividendThreshold[1]}%
          </Typography>
          <Slider
            size="small"
            value={dividendThreshold}
            onChange={handleChangeDividend}
            aria-labelledby="dividend-slider"
            valueLabelDisplay="auto"
            getAriaValueText={(value) => `${value[0]}% - ${value[1]}%`}
            min={0}
            max={20}
            sx={{ width: '80%' }}
          />
        </Grid>
        <Grid item md={2}>
          <Typography id="payout-ratio-slider" variant="body2" gutterBottom>
            Payout Ratio:<br></br> {payoutRatio[0]}% - {payoutRatio[1]}%
          </Typography>
          <Slider
            size="small"
            value={payoutRatio}
            onChange={handleChangePayoutRatio}
            aria-labelledby="payout-ratio-slider"
            valueLabelDisplay="auto"
            getAriaValueText={(value) => `${value[0]}% - ${value[1]}%`}
            min={0}
            max={100}
            sx={{ width: '80%' }}
          />
        </Grid>
        <Grid item md={2}>
          <Typography id="pe-ratio-slider" variant="body2" gutterBottom>
            PE Ratio: {peRatio[0]} - {peRatio[1]}
          </Typography>
          <Slider
            size="small"
            value={peRatio}
            onChange={handleChangePERatio}
            aria-labelledby="pe-ratio-slider"
            valueLabelDisplay="auto"
            getAriaValueText={(value) => `${value[0]} - ${value[1]}`}
            min={0}
            max={100}
            sx={{ width: '80%' }}
          />
        </Grid>
      </Grid>
      <Paper sx={{ p: 2, margin: 'auto', flexGrow: 1, backgroundColor: 'transparent', boxShadow: 'none', mt: 2 }}>
        <TableContainer component={Paper} sx={{ boxShadow: 'none' }}>
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead sx={{ backgroundColor: theme => theme.palette.primary.main, color: 'white' }}>
              <TableRow>
                <TableCell>Stock Name</TableCell>
                <TableCell align="right">Market Cap</TableCell>
                <TableCell align="right">Industry</TableCell>
                <TableCell align="right">Dividend Yield</TableCell>
                <TableCell align="right">Payout Ratio</TableCell>
                <TableCell align="right">PE Ratio</TableCell>
                <TableCell align="right">Action</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {loading ? (
                <TableRow>
                  <TableCell colSpan={7}>
                    <Skeleton variant="rectangular" height={200} animation="wave" />
                  </TableCell>
                </TableRow>
              ) : (
                filteredStocks.map((stock, index) => (
                  <TableRow key={index} hover style={{ cursor: 'pointer' }} onClick={() => handleRowClick(stock.primary_ticker)}>
                    <TableCell component="th" scope="row">
                      <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        <ReactCountryFlag
                          countryCode={stock.country_iso}
                          svg
                          style={{
                            width: '2em',
                            height: '2em',
                            marginRight: '0.5em',
                          }}
                          title={stock.country_iso}
                        />
                        <Box sx={{ display: 'flex', flexDirection: 'column' }}>
                          <Typography variant="body1">{stock.name}</Typography>
                          <Typography variant="body2" color="textSecondary">{stock.primary_ticker} - {stock.exchange}</Typography>
                        </Box>
                      </Box>
                    </TableCell>
                    <TableCell align="right">{formatMarketCap(stock.market_cap)}</TableCell>
                    <TableCell align="right">{stock.industry}</TableCell>
                    <TableCell align="right">{formatPercentage(parseFloat(stock.dividend_yield) * 100)}</TableCell>
                    <TableCell align="right" style={getPayoutRatioStyle(parseFloat(stock.payout_ratio) * 100)}>{formatPercentage(parseFloat(stock.payout_ratio) * 100)}</TableCell>
                    <TableCell align="right">{parseFloat(stock.pe_ratio).toFixed(2)}</TableCell>
                    <TableCell align="right">
                      <IconButton onClick={(event) => handleFollowClick(event, stock.primary_ticker)}>
                        {followedStocks.includes(stock.primary_ticker) ? <FavoriteIcon color="primary" /> : <FavoriteBorderIcon color="disabled" />}
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </div>
  );
}

export default DividendScreener;
