import React, { createContext, useState, useEffect } from 'react';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [showLoginSuccessAlert, setShowLoginSuccessAlert] = useState(false); // Add this line
    const [showLogoutAlert, setShowLogoutAlert] = useState(false); // Add this line


    useEffect(() => {
        // Check the storage if the user is logged in when the app loads
        const token = localStorage.getItem('token');
        setIsAuthenticated(!!token);
    }, []);

    return (
        <AuthContext.Provider 
            value={{ 
                isAuthenticated, 
                setIsAuthenticated, 
                showLoginSuccessAlert, 
                setShowLoginSuccessAlert,
                showLogoutAlert, // Add this
                setShowLogoutAlert // Add this
            }}>
            {children}
        </AuthContext.Provider>
    );
};
