import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Navbar from 'react-bootstrap/Navbar';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import MuiButton from '@mui/material/Button';
import MenuDrawer from './MenuDrawer'; // Import the MenuDrawer component

function Header() {
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);

  const handleDrawerOpen = () => {
    setIsDrawerOpen(true);
  };

  const handleDrawerClose = () => {
    setIsDrawerOpen(false);
  };

  return (
    <Navbar expand="lg" className="bg-body-tertiary" style={{ boxShadow: '0 4px 4px 0 rgba(0,0,0,.2)' }}>
      <Container className="container-fluid header">
        <Navbar.Brand as={Link} to="/">Navbar scroll</Navbar.Brand>
        <IconButton edge="start" color="inherit" aria-label="menu" onClick={handleDrawerOpen}>
          <MenuIcon />
        </IconButton>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll" className="justify-content-end">
          <Form className="d-flex me-2">
            <Form.Control
              type="search"
              placeholder="Search"
              className="me-2"
              aria-label="Search"
            />
            <MuiButton variant="outlined" color="inherit">Search</MuiButton>
          </Form>
          <MuiButton variant="outlined" color="inherit" component={Link} to="/login" className="me-2">Login</MuiButton>
          <MuiButton variant="outlined" color="inherit" component={Link} to="/register">Register</MuiButton>
        </Navbar.Collapse>
      </Container>
      <MenuDrawer isOpen={isDrawerOpen} onDrawerClose={handleDrawerClose} />
    </Navbar>
  );
}

export default Header;
