import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import MenuIcon from '@mui/icons-material/Menu';
import MenuDrawer from './MenuDrawer'; // Ensure the path is correct

function Header() {
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);

  // Handles the opening of the drawer
  const handleDrawerOpen = () => {
    setIsDrawerOpen(true);
  };

  // Handles the closing of the drawer
  const handleDrawerClose = () => {
    setIsDrawerOpen(false);
  };

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static">
        <Toolbar>
          <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen} // Trigger the drawer open
            sx={{ mr: 2 }}
          >
            <MenuIcon />
          </IconButton>
          <Typography
            variant="h6"
            component={Link}
            to="/"
            sx={{ flexGrow: 1, textDecoration: 'none', color: 'inherit' }}
          >
            Bull Street
          </Typography>
        </Toolbar>
      </AppBar>
      <MenuDrawer isOpen={isDrawerOpen} onDrawerClose={handleDrawerClose} />
    </Box>
  );
}

export default Header;
