import React, { useState, useEffect } from 'react';
import {
  Drawer,
  Button,
  List,
  ListItem,
  ListItemText,
  TextField,
  IconButton,
  Typography,
  CircularProgress,
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';

const MyNotesDrawer = ({ open, onClose, stockId }) => {
  const [notes, setNotes] = useState([]);
  const [noteText, setNoteText] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (open) {
      setIsLoading(true);
      fetch(`https://django-stocks-ecbc6bc5e208.herokuapp.com/notes/?stock=${stockId}`, {
        method: 'GET',
        headers: {
          'Authorization': `Token 13502af70a55d1fcddf7c094e4418c65904ef6eb`, // Replace with your token
        },
      })
        .then(response => response.json())
        .then(data => {
          setNotes(data);
          setIsLoading(false);
        })
        .catch(error => {
          console.error('Error fetching notes:', error);
          setIsLoading(false);
        });
    }
  }, [open, stockId]);

  const addNote = () => {
    if (noteText.trim() !== '') {
      const newNote = {
        stock: stockId,
        content: noteText,
      };

      setIsLoading(true);
      fetch('https://django-stocks-ecbc6bc5e208.herokuapp.com/notes/', {
        method: 'POST',
        headers: {
          'Authorization': `Token 13502af70a55d1fcddf7c094e4418c65904ef6eb`, // Replace with your token
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newNote),
      })
        .then(response => response.json())
        .then(data => {
          setNotes([...notes, data]);
          setNoteText('');
          setIsLoading(false);
        })
        .catch(error => {
          console.error('Error adding note:', error);
          setIsLoading(false);
        });
    }
  };

  const deleteNote = (noteId) => {
    setIsLoading(true);
    fetch(`https://django-stocks-ecbc6bc5e208.herokuapp.com/notes/${noteId}/`, {
      method: 'DELETE',
      headers: {
        'Authorization': `Token 13502af70a55d1fcddf7c094e4418c65904ef6eb`, // Replace with your token
      },
    })
      .then(() => {
        setNotes(notes.filter(note => note.id !== noteId));
        setIsLoading(false);
      })
      .catch(error => {
        console.error('Error deleting note:', error);
        setIsLoading(false);
      });
  };

  return (
    <Drawer anchor="right" open={open} onClose={onClose}>
      <div style={{ width: 350, padding: 16 }}>
        <IconButton onClick={onClose} style={{ position: 'absolute', right: 8, top: 8 }}>
          <CloseIcon />
        </IconButton>
        <Typography variant="h6" style={{ marginBottom: 16 }}>My Notes</Typography>
        <TextField
          label="Add a note"
          fullWidth
          value={noteText}
          onChange={(e) => setNoteText(e.target.value)}
          variant="outlined"
          style={{ marginBottom: 16 }}
        />
        <Button
          variant="contained"
          color="primary"
          startIcon={<AddIcon />}
          onClick={addNote}
          fullWidth
          disabled={isLoading}
        >
          Add Note
        </Button>
        {isLoading && <CircularProgress />}
        <List>
          {notes.map((note) => (
            <ListItem key={note.id}>
              <ListItemText
                primary={note.content}
                secondary={new Date(note.created_at).toLocaleDateString()}
              />
              <IconButton edge="end" onClick={() => deleteNote(note.id)}>
                <DeleteIcon />
              </IconButton>
            </ListItem>
          ))}
        </List>
      </div>
    </Drawer>
  );
};

export default MyNotesDrawer;
