import React, { useState } from 'react';
import {
  Typography,
  Paper,
  Box,
  Skeleton,
  Button
} from '@mui/material';

const Description = ({ text, maxLines }) => {
  const [expanded, setExpanded] = useState(false);

  const toggleExpand = () => {
    setExpanded(!expanded);
  };

  // Calculate maximum characters for 2 lines based on font size and line height
  const maxCharacters = maxLines * 32; // Adjust as needed based on your font size and line height

  return (
    <Paper elevation={3} sx={{ margin: 'auto', overflow: 'hidden' }}>
      <Box p={2}>
        <Typography
          style={{
            maxHeight: expanded ? 'none' : `${maxLines * 1.2}em`,
            overflow: 'hidden',
            lineHeight: '1.2', // Adjust line height as needed
            textOverflow: expanded ? 'inherit' : 'ellipsis',
            WebkitLineClamp: expanded ? 'inherit' : maxLines,
            WebkitBoxOrient: 'vertical'
          }}
        >
          {text ? (
            <span>
              {text}
              {!expanded && text.length > maxCharacters && (
                <span>
                  ...
                  <Button onClick={toggleExpand} size="small">
                    {expanded ? 'Read less' : 'Read more'}
                  </Button>
                </span>
              )}
            </span>
          ) : (
            <Skeleton variant="text" width={100} />
          )}
        </Typography>
      </Box>
    </Paper>
  );
};

export default Description;
