import React, { useState } from 'react';
import {
  Typography,
  Paper,
  Box,
  Skeleton,
  Button
} from '@mui/material';

const Description = ({ text, maxLines }) => {
  const [expanded, setExpanded] = useState(false);

  const toggleExpand = () => {
    setExpanded(!expanded);
  };

  return (
    <Paper elevation={3} sx={{ margin: 'auto', overflow: 'hidden' }}>
      <Box p={2} style={{ maxHeight: expanded ? 'none' : `${maxLines * 1.2}em`, overflow: 'hidden' }}>
        <Typography>
          {text ? (
            <span>
              {text}
              {!expanded && text.length > maxLines * 40 && (
                <span>
                  ...
                  <Button onClick={toggleExpand} size="small">
                    {expanded ? 'Read less' : 'Read more'}
                  </Button>
                </span>
              )}
            </span>
          ) : (
            <Skeleton variant="text" width={100} />
          )}
        </Typography>
      </Box>
    </Paper>
  );
};

export default Description;
