import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';

import MenuDrawer from './MenuDrawer'; // Import the MenuDrawer component

const Search = styled('div')(({ theme }) => ({
  position: 'relative',
  borderRadius: theme.shape.borderRadius,
  backgroundColor: theme.palette.common.white,
  marginLeft: 0,
  width: 'auto',
  [theme.breakpoints.up('sm')]: {
    marginLeft: theme.spacing(1),
    width: 'auto',
  },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: '100%',
  position: 'absolute',
  pointerEvents: 'none',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: 'inherit',
  '& .MuiInputBase-input': {
    padding: theme.spacing(1, 1, 1, 0),
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: '12ch',
      '&:focus': {
        width: '20ch',
      },
    },
  },
}));

function Header() {
  const [isDrawerOpen, setIsDrawerOpen] = useState(false); // State for the drawer

  const handleDrawerOpen = () => {
    setIsDrawerOpen(true);
  };

  const handleDrawerClose = () => {
    setIsDrawerOpen(false);
  };

  return (
    <Box className="content-container"> {/* Wrap the AppBar in a Box with the content-container class */}
      <AppBar position="static" sx={{ width: '100%' }}> {/* Ensure AppBar is full width */}
        <Toolbar>
          <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            onClick={handleDrawerOpen} // Open the drawer when the icon is clicked
            sx={{ mr: 2 }}
          >
            <MenuIcon />
          </IconButton>
          <MenuDrawer isOpen={isDrawerOpen} onDrawerClose={handleDrawerClose} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Navbar scroll
          </Typography>
          <Search sx={{ flexGrow: 1 }}> {/* Give some space for the search bar */}
            <SearchIconWrapper>
              <SearchIcon />
            </SearchIconWrapper>
            <StyledInputBase
              placeholder="Search stocksâ€¦" // Add placeholder text
              inputProps={{ 'aria-label': 'search' }}
            />
          </Search>
          <Button variant="outlined" color="inherit" component={Link} to="/login">Login</Button>
          <Button variant="outlined" color="inherit" component={Link} to="/register">Register</Button>
        </Toolbar>
      </AppBar>
    </Box>
  );
}

export default Header;
