import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import InputBase from '@mui/material/InputBase';
import Badge from '@mui/material/Badge';
import MailIcon from '@mui/icons-material/Mail';
import NotificationsIcon from '@mui/icons-material/Notifications';
import { styled, alpha } from '@mui/material/styles';
import MenuDrawer from './MenuDrawer';
import Container from '@mui/material/Container';
import axios from 'axios';
import Alert from '@mui/material/Alert';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import AccountCircle from '@mui/icons-material/AccountCircle';
import MoreIcon from '@mui/icons-material/MoreVert';
import { AuthContext } from '../../context/AuthContext';
import Autocomplete from '@mui/material/Autocomplete';
import CircularProgress from '@mui/material/CircularProgress';
import TextField from '@mui/material/TextField';
import useDebounce from './useDebounce';


const Search = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    '&:hover': {
        backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    marginRight: theme.spacing(2),
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(3),
        width: 'auto',
    },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: 'inherit',
    '& .MuiInputBase-input': {
        padding: theme.spacing(1, 1, 1, 0),
        paddingLeft: `calc(1em + ${theme.spacing(4)})`,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: '12ch',
            '&:focus': {
                width: '20ch',
            },
        },
    },
}));

function Header() {
    const [isDrawerOpen, setIsDrawerOpen] = useState(false);
    const [anchorEl, setAnchorEl] = useState(null);
    const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = useState(null);
    const {
        isAuthenticated,
        setIsAuthenticated,
        showLoginSuccessAlert,
        setShowLoginSuccessAlert,
        showLogoutAlert, 
        setShowLogoutAlert
    } = useContext(AuthContext);
    const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);
    const navigate = useNavigate();
    const [searchInput, setSearchInput] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const debouncedSearchInput = useDebounce(searchInput, 500);    

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsAuthenticated(!!token);
        // Reset login success alert when Header mounts
        setShowLoginSuccessAlert(false);
    }, [setIsAuthenticated, setShowLoginSuccessAlert]);

    // useEffect for handling search
    useEffect(() => {
        if (debouncedSearchInput.length >= 3) { // Only search when input length is 3 or more characters
            setIsLoading(true);
            axios.get(`https://django-stocks-ecbc6bc5e208.herokuapp.com/api/search_stocks/?query=${encodeURIComponent(debouncedSearchInput)}`, {
                headers: {
                    'Authorization': `Token 13502af70a55d1fcddf7c094e4418c65904ef6eb`,
                },
            }).then(response => {
                setSearchResults(response.data || []);
                setIsLoading(false);
            }).catch(error => {
                console.error('Search Error: ', error);
                setIsLoading(false);
            });
        } else {
            setSearchResults([]); // Clear results if input is less than 3 characters
        }
    }, [debouncedSearchInput])    

    const handleProfileMenuOpen = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleMobileMenuClose = () => {
        setMobileMoreAnchorEl(null);
    };

    const handleMenuClose = () => {
        setAnchorEl(null);
        handleMobileMenuClose();
    };

    const handleMobileMenuOpen = (event) => {
        setMobileMoreAnchorEl(event.currentTarget);
    };

    const handleLogout = async () => {
        try {
            await axios.post('https://django-stocks-ecbc6bc5e208.herokuapp.com/auth/logout/');
            localStorage.removeItem('token');
            setIsAuthenticated(false);

            // Set the logout alert state to true
            setShowLogoutAlert(true); 

            // Navigate to the login page
            navigate('/login', { replace: true }); // Added { replace: true } to replace the current entry in the history stack
        } catch (error) {
            console.error('Logout Error: ', error);
        }
    };


    const renderMenu = (
        <Menu
            anchorEl={anchorEl}
            anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
            }}
            id="primary-search-account-menu"
            keepMounted
            transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
            }}
            open={Boolean(anchorEl)}
            onClose={handleMenuClose}
        >
            {isAuthenticated ? [
                <MenuItem key="profile" onClick={handleMenuClose}>Profile</MenuItem>,
                <MenuItem key="account" onClick={handleMenuClose}>My account</MenuItem>,
                <MenuItem key="logout" onClick={handleLogout}>Logout</MenuItem>
            ] : [
                <MenuItem key="login" component={Link} to="/login" onClick={handleMenuClose}>Login</MenuItem>,
                <MenuItem key="register" component={Link} to="/signup" onClick={handleMenuClose}>Register</MenuItem>
            ]}
        </Menu>
    );

    const renderMobileMenu = (
        <Menu
            anchorEl={mobileMoreAnchorEl}
            anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
            }}
            id="primary-search-account-menu-mobile"
            keepMounted
            transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
            }}
            open={isMobileMenuOpen}
            onClose={handleMobileMenuClose}
        >
            {isAuthenticated ? [
                <MenuItem key="profile" onClick={handleMenuClose}>Profile</MenuItem>,
                <MenuItem key="account" onClick={handleMenuClose}>My account</MenuItem>,
                <MenuItem key="logout" onClick={handleLogout}>Logout</MenuItem>
            ] : [
                <MenuItem key="login" component={Link} to="/login" onClick={handleMobileMenuClose}>Login</MenuItem>,
                <MenuItem key="register" component={Link} to="/signup" onClick={handleMobileMenuClose}>Register</MenuItem>
            ]}
        </Menu>
    );


    return (
        <Box sx={{ flexGrow: 1 }}>
            <AppBar className="stickyHeader">
                <Container maxWidth="xl" style={{ maxWidth: '1280px', margin: '0 auto' }}>
                    <Toolbar>
                        <IconButton
                            size="large"
                            edge="start"
                            color="inherit"
                            aria-label="open drawer"
                            sx={{ mr: 2 }}
                            onClick={() => setIsDrawerOpen(true)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography
                            variant="h6"
                            noWrap
                            component={Link}
                            to="/"
                            sx={{ display: { xs: 'none', sm: 'block' }, flexGrow: 1, textDecoration: 'none', color: 'inherit' }}
                        >
                            Bull Street
                        </Typography>
                        <Search>
                                        <SearchIconWrapper>
                                            <SearchIcon />
                                        </SearchIconWrapper>
                                        <Autocomplete
                                            freeSolo
                                            id="search-autocomplete"
                                            options={searchResults}
                                            loading={isLoading}
                                            onInputChange={(event, newInputValue) => {
                                                setSearchInput(newInputValue);
                                            }}
                                            onChange={(event, value) => {
                                                if (value && value.primary_ticker) {
                                                    navigate(`/stocks/${value.primary_ticker}`);
                                                }
                                            }}
                                            getOptionLabel={(option) => `${option.name} (${option.primary_ticker})`}
                                            renderInput={(params) => (
                                                <TextField
                                                    {...params}
                                                    label="Search stocks..."
                                                    variant="outlined"
                                                    InputProps={{
                                                        ...params.InputProps,
                                                        endAdornment: (
                                                            <React.Fragment>
                                                                {isLoading ? <CircularProgress color="inherit" size={20} /> : null}
                                                                {params.InputProps.endAdornment}
                                                            </React.Fragment>
                                                        ),
                                                    }}
                                                />
                                            )}
                                        />
                                    </Search>
                        <Box sx={{ display: { xs: 'none', md: 'flex' } }}>
                            <IconButton size="large" aria-label="show 4 new mails" color="inherit">
                                <Badge badgeContent={4} color="error">
                                    <MailIcon />
                                </Badge>
                            </IconButton>
                            <IconButton
                                size="large"
                                aria-label="show 17 new notifications"
                                color="inherit"
                            >
                                <Badge badgeContent={17} color="error">
                                    <NotificationsIcon />
                                </Badge>
                            </IconButton>
                            <IconButton
                                size="large"
                                edge="end"
                                aria-label="account of current user"
                                aria-controls="primary-search-account-menu"
                                aria-haspopup="true"
                                onClick={handleProfileMenuOpen}
                                color="inherit"
                            >
                                <AccountCircle />
                            </IconButton>
                        </Box>
                        <Box sx={{ display: { xs: 'flex', md: 'none' } }}>
                            <IconButton
                                size="large"
                                aria-label="show more"
                                aria-controls="primary-search-account-menu-mobile"
                                aria-haspopup="true"
                                onClick={handleMobileMenuOpen}
                                color="inherit"
                            >
                                <MoreIcon />
                            </IconButton>
                        </Box>
                    </Toolbar>
                </Container>
                <MenuDrawer isOpen={isDrawerOpen} onDrawerClose={() => setIsDrawerOpen(false)} />
            </AppBar>
            {renderMobileMenu}
            {renderMenu}
            {showLoginSuccessAlert && (
                <Alert className="alert" severity="success" onClose={() => setShowLoginSuccessAlert(false)}>
                    Logged in successfully!
                </Alert>
            )}
            {showLogoutAlert && (
                <Alert className="alert" severity="success" onClose={() => setShowLogoutAlert(false)}>
                    Logged out successfully!
                </Alert>
            )}
        </Box>
    );
}

export default Header;
