import React from 'react';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import { styled } from '@mui/material/styles';

const TickerWidgetContainer = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  textAlign: 'center',
  color: theme.palette.text.secondary,
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
}));

const Ticker = ({ proName, title, description }) => {
  const containerRef = React.useRef();

  React.useEffect(() => {
    if (containerRef.current) {
      containerRef.current.innerHTML = ''; // Clear previous content

      const script = document.createElement('script');
      script.src = 'https://s3.tradingview.com/external-embedding/embed-widget-single-quote.js';
      script.async = true;
      script.type = 'text/javascript';
      script.innerHTML = JSON.stringify({
        "symbol": proName,
        "width": "100%",
        "height": "100%",
        "isTransparent": false,
        "colorTheme": "light",
        "locale": "en"
      });

      containerRef.current.appendChild(script);
    }
  }, [proName]); // Depend on proName to re-run the effect if it changes

  return (
    <TickerWidgetContainer ref={containerRef}>
      <div className="tradingview-widget-container__widget"></div>
      {title || description ? (
        <div className="tradingview-widget-description">
          {title || description}
        </div>
      ) : null}
    </TickerWidgetContainer>
  );
};

const tickers = [
  { proName: "FOREXCOM:SPXUSD", title: "S&P 500" },
  { proName: "NASDAQ:IXIC", description: "NASDAQ" },
  { proName: "SPREADEX:FTSE", description: "FTSE 100" },
  { proName: "CAPITALCOM:RTY", description: "RUSSELL 2000" },
  { proName: "XETR:DAX", description: "DAX" },
  { proName: "SPREADEX:CAC", description: "CAC" },
];

const TickersRow = () => {
  return (
    <Grid container spacing={2}>
      {tickers.map((ticker, index) => (
        <Grid item xs={2} key={index}>
          <Ticker {...ticker} />
        </Grid>
      ))}
    </Grid>
  );
};

export default TickersRow;
