import React from 'react';
import Typography from '@mui/material/Typography';
import Slider from '@mui/material/Slider';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import { useTheme } from '@mui/material/styles';

const AnalystOverallRating = ({ ratings }) => {
  const theme = useTheme(); // This hook allows you to use the current theme
  
const calculateOverallRating = (ratings) => {
  console.log('Ratings received:', ratings); // Debug: Log the ratings received

  // Define the weight for each rating, reversed as requested
  const weights = {
    strong_buy: 5,
    buy: 4,
    hold: 3,
    sell: 2,
    strong_sell: 1
  };

  // Calculate the weighted sum of ratings
  const weightedSum = Object.keys(ratings).reduce((sum, key) => {
    if (typeof ratings[key] !== 'number') {
      console.error(`Invalid rating for ${key}:`, ratings[key]); // Debug: Log any invalid ratings
      return sum;
    }
    return sum + (weights[key] * ratings[key]);
  }, 0);

  // Calculate the total number of analysts
  const numberOfAnalysts = Object.values(ratings).reduce((sum, num) => {
    if (typeof num !== 'number') {
      console.error('Invalid number of analysts:', num); // Debug: Log any invalid number of analysts
      return sum;
    }
    return sum + num;
  }, 0);

  if (numberOfAnalysts === 0) {
    console.error('Total number of analysts is zero.'); // Debug: Log if the number of analysts is zero
    return 'N/A'; // Return 'N/A' if no analysts
  }

  // Return the weighted average rounded to one decimal place
  const overallRating = (weightedSum / numberOfAnalysts).toFixed(1);
  console.log('Overall Rating calculated:', overallRating); // Debug: Log the calculated overall rating
  return overallRating;
};

  // Create marks for the Slider
  const marks = [
    { value: 1, label: 'Strong Sell' },
    { value: 2, label: 'Sell' },
    { value: 3, label: 'Hold' },
    { value: 4, label: 'Buy' },
    { value: 5, label: 'Strong Buy' },
  ];

  return (
    <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
      <Typography id="analyst-rating-slider" gutterBottom>
        Recommendation Rating
      </Typography>
      <Box sx={{ m: 3 }}>
        <Slider
          value={parseFloat(overallRating)}
          aria-labelledby="analyst-rating-slider"
          valueLabelDisplay="auto"
          step={0.1}
          marks={marks}
          min={1}
          max={5}
          disabled
          sx={{
            color: theme.palette.primary.main, // Use the primary color from the theme
            '& .MuiSlider-thumb': {
              backgroundColor: theme.palette.common.white, // Thumb color
            },
            '& .MuiSlider-markLabel': {
              color: theme.palette.text.primary, // Marks labels color
            },
            '& .MuiSlider-valueLabel': {
              backgroundColor: 'transparent',
              color: theme.palette.primary.main,
              '& *': {
                background: 'transparent',
                color: theme.palette.primary.main,
              },
            },
          }}
        />
      </Box>
      <Typography variant="h5" component="h4" align="center" color="primary">
        {`Overall Rating: ${overallRating} based on ${Object.values(ratings).reduce((a, b) => a + b, 0)} opinions`}
      </Typography>
    </Paper>
  );
};

export default AnalystOverallRating;
