import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Add this import
import axios from 'axios';
import { Slider, Typography, Table, TableBody, TableCell, TableHead, TableRow, Paper } from '@mui/material';

function DividendScreener() {
    const { primary_ticker } = useParams(); // Get the primary_ticker from the URL
    const [stocks, setStocks] = useState([]); // Initialize with an empty array
    const [dividendThreshold, setDividendThreshold] = useState(0);

    useEffect(() => {
        if (!primary_ticker) {
            // Handle the case where primary_ticker is not available in the URL
            return;
        }

        const formattedTicker = primary_ticker.replace('-', '.');

        const fetchStockData = async () => {
            try {
                const response = await fetch(`https://django-stocks-ecbc6bc5e208.herokuapp.com/api/stocks/${formattedTicker}/`, {
                    headers: {
                        'Authorization': `Token 13502af70a55d1fcddf7c094e4418c65904ef6eb`,
                    },
                });

                if (response.ok) {
                    const data = await response.json();
                    setStocks(data);
                } else {
                    console.error('Error fetching stock data:', response.status);
                }
            } catch (error) {
                console.error('Error fetching stock data:', error);
            }
        };

        fetchStockData();
    }, [primary_ticker]);

    const handleSliderChange = (event, newValue) => {
        setDividendThreshold(newValue);
    };

    const filteredStocks = stocks.filter(stock => stock.dividend_yield >= dividendThreshold);

    return (
        <div>
            <Typography id="dividend-slider" gutterBottom>
                Dividend Yield Threshold: {dividendThreshold}%
            </Typography>
            <Slider
                value={dividendThreshold}
                onChange={handleSliderChange}
                aria-labelledby="dividend-slider"
                valueLabelDisplay="auto"
                step={0.1}
                marks
                min={0}
                max={20} // Adjust based on your data
            />
            <Paper>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Name</TableCell>
                            <TableCell align="right">Dividend Yield (%)</TableCell>
                            <TableCell align="right">Market Cap</TableCell>
                            <TableCell align="right">P/E Ratio</TableCell>
                            {/* Add more columns as needed */}
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {filteredStocks.map((stock) => (
                            <TableRow key={stock.id}>
                                <TableCell component="th" scope="row">
                                    {stock.name}
                                </TableCell>
                                <TableCell align="right">{stock.dividend_yield}</TableCell>
                                <TableCell align="right">{stock.market_capitalization}</TableCell>
                                <TableCell align="right">{stock.pe_ratio}</TableCell>
                                {/* Add more cells as needed */}
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </Paper>
        </div>
    );
}

export default DividendScreener;
