import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';

import MenuDrawer from './MenuDrawer'; // Import the MenuDrawer component

const Search = styled('div')(({ theme }) => ({
  position: 'relative',
  borderRadius: theme.shape.borderRadius,
  backgroundColor: theme.palette.common.white,
  marginLeft: 0,
  width: 'auto',
  [theme.breakpoints.up('sm')]: {
    marginLeft: theme.spacing(1),
    width: 'auto',
  },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: '100%',
  position: 'absolute',
  pointerEvents: 'none',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: 'inherit',
  '& .MuiInputBase-input': {
    padding: theme.spacing(1, 1, 1, 0),
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: '12ch',
      '&:focus': {
        width: '20ch',
      },
    },
  },
}));

function Header() {
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);

  return (
    <AppBar position="static" color="primary" sx={{ width: '100%' }}>
      <Toolbar sx={{ justifyContent: 'space-between', padding: 0 }}> {/* Remove padding if it exists */}
        <Box sx={{ flex: 1, display: 'flex', alignItems: 'center', paddingLeft: 2 }}> {/* Add padding to match the content container */}
          <IconButton size="large" edge="start" color="inherit" aria-label="menu" onClick={() => setIsDrawerOpen(true)}>
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" component="div">
            Navbar scroll
          </Typography>
        </Box>
        <Box className="content-container" sx={{ maxWidth: '1920px', display: 'flex', flex: 3, justifyContent: 'center' }}>
          <Search sx={{ marginRight: 2 }}>
            <SearchIconWrapper>
              <SearchIcon />
            </SearchIconWrapper>
            <StyledInputBase placeholder="Search stocksâ€¦" inputProps={{ 'aria-label': 'search' }} />
          </Search>
        </Box>
        <Box sx={{ flex: 1, display: 'flex', justifyContent: 'flex-end', paddingRight: 2 }}> {/* Add padding to match the content container */}
          <Button variant="outlined" color="inherit" component={Link} to="/login">Login</Button>
          <Button variant="outlined" color="inherit" component={Link} to="/register" sx={{ marginLeft: 2 }}>Register</Button>
        </Box>
      </Toolbar>
      <MenuDrawer isOpen={isDrawerOpen} onDrawerClose={() => setIsDrawerOpen(false)} />
    </AppBar>
  );
}

export default Header;
