import React from 'react';
import { Typography, TableContainer, Table, TableRow, TableCell, Paper, Box, Link, makeStyles } from '@mui/material';

const useStyles = makeStyles((theme) => ({
  keyCell: {
    fontWeight: 'bold',
    paddingRight: theme.spacing(1),
  },
  valueCell: {
    paddingLeft: theme.spacing(1),
  },
}));

const BasicStats = ({ data }) => {
  const classes = useStyles();

  const columns = [
    { key: 'Sector', value: data.sector },
    { key: 'Industry', value: data.industry },
    { key: 'Exchange', value: data.exchange },
    { key: 'Country', value: data.country_name },
    { key: 'ISIN', value: data.isin },
    { key: 'Ticker', value: data.primary_ticker },
    { key: 'Year End', value: data.fiscal_year_end },
    {
      key: 'Web URL',
      value: (
        <Link href={data.web_url} target="_blank" rel="noopener noreferrer">
          Link
        </Link>
      ),
    },
  ];

  return (
    <Paper elevation={3} sx={{ margin: 'auto', overflow: 'hidden', p: 2 }}>
      <TableContainer>
        <Table aria-label="Basic Stats Table">
          <tbody>
            {columns.map((column, index) => (
              <TableRow key={index}>
                <TableCell className={classes.keyCell}>{column.key}:</TableCell>
                <TableCell className={classes.valueCell}><strong>{column.value}</strong></TableCell>
              </TableRow>
            ))}
          </tbody>
        </Table>
      </TableContainer>
    </Paper>
  );
};

export default BasicStats;
