import React from 'react';
import { Card, CardContent, Typography, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Divider, Box } from '@mui/material';

const StockHighlights = ({ highlights }) => {
  // Assuming highlights is an object with keys as the title and values as the content
  // For example: { "Target Price": "$200", "EPS": "3.45" }
  const highlightKeys = Object.keys(highlights);

  return (
    <Card>
      <Box sx={{ bgcolor: 'primary.main', color: 'primary.contrastText', p: 2, textAlign: 'center' }}>
        <Typography variant="h6">Highlights</Typography>
      </Box>
      <Divider light />
      <CardContent>
        <TableContainer component={Paper}>
          <Table aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell>Highlight</TableCell>
                <TableCell align="right">Value</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {highlightKeys.map((key) => (
                <TableRow
                  key={key}
                  sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                >
                  <TableCell component="th" scope="row">
                    {key}
                  </TableCell>
                  <TableCell align="right">{highlights[key]}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </CardContent>
    </Card>
  );
};

export default StockHighlights;
